package kmk1211;

public class d11c1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=0, num1=-1;
		int sum=0; 
		
		while(sum<100) {
			num++;
			if(num%2!=0) {
				sum += num;
			}else {
				sum += num*num1;
			}
						
		}
		

		
		}
	}
