		//각각 값을 저장해보고 for문을 이용하여 저장된 값 출력
		
		double[] num = new double[5];
		num[0] = 5;
		num[1] = 4;
		num[2] = 3;
		num[3] = 2;
		num[4] = 1;
		for(int k=0; k<num.length
				; k++) {
			System.out.println(num[k]);
		}
		
		
